apt-get install fail2ban -y --fix-missing;
mkdir .ssh;
chmod 700 .ssh;
sed -i "s/.*#PasswordAuthentication yes/PasswordAuthentication no/g" "/etc/ssh/sshd_config";
sed -i "s/.*#PermitEmptyPasswords no/PermitEmptyPasswords no/g" "/etc/ssh/sshd_config";
sed -i "s/.*UsePAM yes/UsePAM no/g" "/etc/ssh/sshd_config";
echo "ssh-rsa  rsa-key-dev" >> $HOME/.ssh/authorized_keys;
systemctl restart sshd.service;
sed -i "s/^\[sshd\]/\[sshd\]\nenabled = true\nbantime = 86400\nfindtime = 60m\nmaxretry = 1\nbanaction = iptables-multiport\n/g" "/etc/fail2ban/jail.conf";
sed -i "s/^disable_functions \= /disable_function \= mail,/g" "/etc/php/7.3/fpm/php.ini";
sed -i "s/^disable_functions \= /disable_function \= mail,/g" "/etc/php/7.4/fpm/php.ini";
systemctl enable fail2ban;
systemctl restart php7.3-fpm;
systemctl restart php7.4-fpm;
systemctl restart fail2ban;
rm up.sh;
